// Paso 1: elegir actor
let actoresConDue침oArr = game.actors.filter(a => a.hasPlayerOwner);
if(actoresConDue침oArr.length === 0) {
  ui.notifications.warn("No hay personajes controlados por jugadores en la partida.");
