let actoresOptions = actoresConDue침oArr.map(a => `<option value="${a.id}">${a.name}</option>`).join("");

// Paso 2: mostrar di치logo inicial para seleccionar actor
new Dialog({
  title: "Selecciona personaje",
  content: `<p><b>Selecciona el personaje:</b></p>
            <select id="actor">${actoresOptions}</select>`,
  buttons: {
    siguiente: {
      label: "Siguiente",
      callback: async html => {
        const actorId = html.find("#actor").val();
        const actor = game.actors.get(actorId);
        if (!actor) return ui.notifications.error("Actor no encontrado");

        // Generar opciones SOLO con las habilidades listadas
        let habilidadesOptions = Object.entries(habilidades)
          .map(([key, nombre]) => `<option value="${key}">${nombre}</option>`)
          .join("");

        // Paso 3: mostrar di치logo de habilidades
        new Dialog({
          title: `Solicitar tirada: ${actor.name}`,
          content: `<p><b>Selecciona la habilidad:</b></p>
                    <select id="habilidad">${habilidadesOptions}</select>`,
          buttons: {
            enviar: {
              label: "Enviar Solicitud",
              callback: async html2 => {
                const habilidad = html2.find("#habilidad").val();

                // Buscar jugador con permiso OWNER
                let player = game.users.players.find(u => actor.testUserPermission(u, OWNER_PERMISSION_LEVEL));
                if (!player) return ui.notifications.error("No se encontr칩 jugador que controle este personaje.");

                // Mensaje privado con bot칩n
                const chatContent = `
                  <p><b>${game.user.name}</b> te solicita una tirada de <b>${habilidades[habilidad]}</b> para <b>${actor.name}</b>.</p>
                  <button class="roll-solicitud" data-actor-id="${actor.id}" data-skill="${habilidad}">游 Lanzar Tirada</button>
                `;

                await ChatMessage.create({
                  user: player.id,
                  whisper: [player.id],
                  speaker: { alias: "Solicitud de Tirada" },
                  content: chatContent
                });

                ui.notifications.info(`Solicitud enviada a ${player.name}`);
              }
